package types_test

import (
	"encoding/hex"
	"testing"

	bbn "github.com/babylonlabs-io/babylon/v4/types"
	btcctypes "github.com/babylonlabs-io/babylon/v4/x/btccheckpoint/types"
	btcchaincfg "github.com/btcsuite/btcd/chaincfg"
	"github.com/stretchr/testify/require"
)

// TestMerkleIndexValidationVulnerability is the PoC test from the vulnerability report
// This test demonstrates that after the fix, forged indices are correctly rejected.
// Before the fix, this test would show that fake indices pass verification (vulnerability).
// After the fix, this test verifies that fake indices are rejected (fix confirmed).
func TestMerkleIndexValidationVulnerability(t *testing.T) {
	headerHex := "0000e0208c3b3ed3aa778eaecdcbe91dae57197ce1baa0d7c33e86d00d0100000000000079ffca6c6b36348c306234dee2fe47bafd76df7e70c95cbdff3efeb81e5abe71ea88b860fcff031a45722027"
	transactions := []string{
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
	}

	headerBytes, err := bbn.NewBTCHeaderBytesFromHex(headerHex)
	require.NoError(t, err)

	header := headerBytes.ToBlockHeader()
	merkleRoot := &header.MerkleRoot

	var transactionBytes [][]byte
	for _, txHex := range transactions {
		txBytes, err := hex.DecodeString(txHex)
		require.NoError(t, err)
		transactionBytes = append(transactionBytes, txBytes)
	}

	t.Logf("Block has %d transactions", len(transactions))

	t.Run("Scenario A: Coinbase check bypass - FIXED", func(t *testing.T) {
		coinbaseIdx := uint(0)
		coinbaseTxBytes := transactionBytes[coinbaseIdx]

		branch, err := btcctypes.CreateProofForIdx(transactionBytes, coinbaseIdx)
		require.NoError(t, err)

		proofDepth := uint(len(branch))
		indexMultiplier := uint32(1 << proofDepth)
		t.Logf("Proof depth = %d, index multiplier = %d", proofDepth, indexMultiplier)

		var proof []byte
		for _, h := range branch {
			proof = append(proof, h.CloneBytes()...)
		}

		coinbaseTx, err := btcctypes.ParseTransaction(coinbaseTxBytes)
		require.NoError(t, err)

		realIndex := uint32(0)
		validWithRealIndex := btcctypes.VerifyInclusionProof(coinbaseTx, merkleRoot, proof, realIndex)
		require.True(t, validWithRealIndex, "Verification with real index 0 should pass")

		fakeIndex := realIndex + indexMultiplier
		validWithFakeIndex := btcctypes.VerifyInclusionProof(coinbaseTx, merkleRoot, proof, fakeIndex)
		require.False(t, validWithFakeIndex, "FIX CONFIRMED: Fake index %d is now rejected", fakeIndex)

		isCoinbaseByRealIndex := realIndex == 0
		isCoinbaseByFakeIndex := fakeIndex == 0
		require.True(t, isCoinbaseByRealIndex)
		require.False(t, isCoinbaseByFakeIndex)

		t.Log("FIX VERIFIED: Multiple fake indices for coinbase are now correctly rejected:")
		for k := uint32(1); k <= 3; k++ {
			fakeIdx := realIndex + k*indexMultiplier
			valid := btcctypes.VerifyInclusionProof(coinbaseTx, merkleRoot, proof, fakeIdx)
			require.False(t, valid, "Fake index %d should be rejected", fakeIdx)
			t.Logf("  index=%d (0 + %d * %d): CORRECTLY REJECTED", fakeIdx, k, indexMultiplier)
		}
	})

	t.Run("Scenario B: Duplicate submission with different indices - FIXED", func(t *testing.T) {
		txIdx := uint(3)
		txBytes := transactionBytes[txIdx]

		branch, err := btcctypes.CreateProofForIdx(transactionBytes, txIdx)
		require.NoError(t, err)

		proofDepth := uint(len(branch))
		indexMultiplier := uint32(1 << proofDepth)

		var proof []byte
		for _, h := range branch {
			proof = append(proof, h.CloneBytes()...)
		}

		tx, err := btcctypes.ParseTransaction(txBytes)
		require.NoError(t, err)

		realIndex := uint32(txIdx)
		validWithRealIndex := btcctypes.VerifyInclusionProof(tx, merkleRoot, proof, realIndex)
		require.True(t, validWithRealIndex)

		t.Log("FIX VERIFIED: Same transaction with fake indices is now correctly rejected:")
		for k := uint32(1); k <= 3; k++ {
			fakeIdx := realIndex + k*indexMultiplier
			valid := btcctypes.VerifyInclusionProof(tx, merkleRoot, proof, fakeIdx)
			require.False(t, valid, "Fake index %d should be rejected", fakeIdx)
			t.Logf("  index=%d (%d + %d * %d): CORRECTLY REJECTED (prevents duplicate SubmissionKey)", fakeIdx, realIndex, k, indexMultiplier)
		}
	})
}

func TestMerkleIndexVulnerabilityWithParseProof(t *testing.T) {
	headerHex := "0000e0208c3b3ed3aa778eaecdcbe91dae57197ce1baa0d7c33e86d00d0100000000000079ffca6c6b36348c306234dee2fe47bafd76df7e70c95cbdff3efeb81e5abe71ea88b860fcff031a45722027"
	transactions := []string{
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
	}

	headerBytes, err := bbn.NewBTCHeaderBytesFromHex(headerHex)
	require.NoError(t, err)

	var transactionBytes [][]byte
	for _, txHex := range transactions {
		txBytes, err := hex.DecodeString(txHex)
		require.NoError(t, err)
		transactionBytes = append(transactionBytes, txBytes)
	}

	txIdx := uint(3)

	branch, err := btcctypes.CreateProofForIdx(transactionBytes, txIdx)
	require.NoError(t, err)

	proofDepth := uint(len(branch))
	indexMultiplier := uint32(1 << proofDepth)
	t.Logf("Proof depth = %d, index multiplier = %d", proofDepth, indexMultiplier)

	var proof []byte
	for _, h := range branch {
		proof = append(proof, h.CloneBytes()...)
	}

	realIndex := uint32(txIdx)
	parsedProof, err := btcctypes.ParseProof(
		transactionBytes[txIdx],
		realIndex,
		proof,
		&headerBytes,
		btcchaincfg.TestNet3Params.PowLimit,
	)
	require.NoError(t, err)
	require.NotNil(t, parsedProof)
	t.Logf("ParseProof with real index %d: SUCCESS", realIndex)

	fakeIndex := realIndex + indexMultiplier
	parsedProofFake, err := btcctypes.ParseProof(
		transactionBytes[txIdx],
		fakeIndex,
		proof,
		&headerBytes,
		btcchaincfg.TestNet3Params.PowLimit,
	)
	require.Error(t, err, "ParseProof with fake index should fail after fix")
	require.Nil(t, parsedProofFake)
	t.Logf("FIX CONFIRMED: ParseProof with fake index %d: CORRECTLY REJECTED", fakeIndex)
}
